// src/components/GoodsForm.tsx

import React, { useState } from "react";
import axios from "axios";

const aspectRatios = [
  "match_input_image", "1:1", "16:9", "9:16", "4:3", "3:4", "3:2", "2:3",
  "4:5", "5:4", "21:9", "9:21", "2:1", "1:2"
];

export default function GoodsForm() {
  const [prompt, setPrompt] = useState("Draw a realistic version of a short-sleeve white color T-shirt with this pig character printed on it.");
  const [image, setImage] = useState<File | null>(null);
  const [preview, setPreview] = useState<string>("");
  const [aspectRatio, setAspectRatio] = useState("1:1");
  const [seed, setSeed] = useState<number>(11);
  const [isRandomSeed, setIsRandomSeed] = useState(false);
  const [safetyTolerance, setSafetyTolerance] = useState(2);
  const [outputUrl, setOutputUrl] = useState("");
  const [loading, setLoading] = useState(false);

  const handleImageUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0] || null;
    if (file) {
      setImage(file);
      setPreview(URL.createObjectURL(file));
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!image) {
      alert("캐릭터 이미지를 업로드하세요.");
      return;
    }

    const finalSeed = isRandomSeed ? Math.floor(Math.random() * 99999999) : seed;

    const formData = new FormData();
    formData.append("prompt", prompt);
    formData.append("input_image", image);
    formData.append("aspect_ratio", aspectRatio);
    formData.append("seed", finalSeed.toString());
    formData.append("safety_tolerance", safetyTolerance.toString());

    setLoading(true);
    setOutputUrl("");

    try {
      // ✅ 우리 백엔드의 /generate-goods 엔드포인트로 요청
      const response = await axios.post("http://localhost:8000/generate-goods", formData);
      setOutputUrl(response.data.output_url);
    } catch (error: any) {
      const errorMessage = error.response?.data?.error || "이미지 생성에 실패했습니다.";
      alert(errorMessage);
      console.error(error);
    } finally {
      setLoading(false);
    }
  };
  
  const handleDownload = async () => {
    // ... (이전과 동일한 다운로드 함수)
    if (!outputUrl) return;
    try {
      const response = await fetch(outputUrl);
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'generated-goods.png');
      document.body.appendChild(link);
      link.click();
      link.parentNode?.removeChild(link);
      window.URL.revokeObjectURL(url);
    } catch (error) {
      console.error("다운로드 실패:", error);
    }
  };

  return (
    <div style={{ padding: 40, maxWidth: 720, margin: "0 auto" }}>
      <h2>웹툰 캐릭터 굿즈 초안 생성</h2>
      
      <form onSubmit={handleSubmit}>
        {/* 프롬프트 */}
        <label>프롬프트를 영어로 입력하세요</label>
        <input value={prompt} onChange={(e) => setPrompt(e.target.value)} style={{ width: '100%', padding: 8, marginBottom: 16 }} />

        {/* 이미지 업로드 */}
        <label>웹툰 캐릭터 이미지를 넣어주세요</label>
        <input type="file" accept="image/*" onChange={handleImageUpload} style={{ width: '100%' }} />
        {preview && <img src={preview} alt="preview" style={{ width: "50%", marginTop: 8 }} />}

        {/* 이미지 비율 */}
        <label style={{ display: 'block', marginTop: 16 }}>이미지 비율</label>
        <select value={aspectRatio} onChange={(e) => setAspectRatio(e.target.value)} style={{ width: '100%', padding: 8, marginBottom: 16 }}>
            {aspectRatios.map(r => <option key={r} value={r}>{r}</option>)}
        </select>

        {/* Seed 설정 */}
        <label>Seed 설정</label>
        <div>
          <input type="radio" id="fixedSeed" name="seedMode" checked={!isRandomSeed} onChange={() => setIsRandomSeed(false)} />
          <label htmlFor="fixedSeed">고정값 사용</label>
          <input type="radio" id="randomSeed" name="seedMode" checked={isRandomSeed} onChange={() => setIsRandomSeed(true)} />
          <label htmlFor="randomSeed">랜덤하게 생성</label>
        </div>
        <input type="number" value={seed} onChange={(e) => setSeed(Number(e.target.value))} disabled={isRandomSeed} style={{ width: '100%', padding: 8, marginBottom: 16 }} />
        
        {/* Safety Tolerance */}
        <label>Safety Tolerance: {safetyTolerance}</label>
        <input type="range" min={0} max={2} value={safetyTolerance} onChange={(e) => setSafetyTolerance(Number(e.target.value))} style={{ width: '100%', marginBottom: 16 }} />

        <button type="submit" disabled={loading} style={{ width: '100%', padding: 12, fontSize: 16, cursor: 'pointer' }}>
            {loading ? "생성 중..." : "굿즈 초안 생성✨"}
        </button>
      </form>

      {/* 결과 표시 */}
      <div style={{ marginTop: 20 }}>
        {loading && <p>이미지 생성 중, 잠시 기다려주세요...</p>}
        {outputUrl && (
          <div>
            <h3>결과</h3>
            <img src={outputUrl} alt="Generated Goods" style={{ width: "100%" }}/>
            <button onClick={handleDownload} style={{ marginTop: 10, padding: 10, cursor: 'pointer' }}>
              📥 이미지 다운로드
            </button>
          </div>
        )}
      </div>
    </div>
  );
}
