const dropZoneStyle: React.CSSProperties = {
  border: "2px dashed #09AA5C",
  borderRadius: "8px",
  padding: "20px",
  backgroundColor: "#f9f9f9",
  color: "#aaa",
  textAlign: "center",
  minHeight: "300px",
  cursor: "pointer",
  transition: "border-color 0.3s",
};

const dropZoneHoverStyle: React.CSSProperties = {
  ...dropZoneStyle,
  borderColor: "#2ecc71",
  backgroundColor: "#f1fff5",
};
