import React, { useState } from "react";
import axios from "axios";
import NavBar from "./NavBar.tsx";

const aspectRatios = [
  "match_input_image", "1:1", "16:9", "9:16", "4:3", "3:4", "3:2", "2:3",
  "4:5", "5:4", "21:9", "9:21", "2:1", "1:2"
];

// Ïä§ÌÉÄÏùº Í∞ùÏ≤¥
const containerStyle: React.CSSProperties = {
  fontFamily: "'Nanum Gothic', sans-serif",
  backgroundColor: "#ffffff",
  minHeight: "100vh",
  margin: 0,
  padding: 0,
  overflowX: "hidden",
};

const mainContentStyle: React.CSSProperties = {
  display: "flex",
  gap: "40px",
  maxWidth: "1440px",
  margin: "0 auto",
  padding: "0 20px",
  alignItems: "flex-start",
  boxSizing: "border-box",
};

// box Ïä§ÌÉÄÏùº Ï∂îÍ∞Ä
const fileInputBoxStyle: React.CSSProperties = {
  width: '100%',
  padding: '8px',
  marginBottom: '8px',
  border: '1px solid #ccc',
  borderRadius: '4px',
};

const imagePreviewFrameStyle: React.CSSProperties = {
  border: "1px solid #ddd",
  borderRadius: "8px",
  padding: "10px",
  marginBottom: "20px",
  backgroundColor: "#f9f9f9",
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
  minHeight: "300px",
  width: "100%",
};

const promptBoxStyle: React.CSSProperties = {
  width: '100%',
  padding: '8px',
  marginBottom: '16px',
  borderRadius: '4px',
  border: '1px solid #ccc',
  minHeight: '40px', // ÎÜíÏù¥ Ï†àÎ∞ò ÏàòÏ§Ä
  resize: 'vertical'
};

const formColumnStyle: React.CSSProperties = {
  flex: 1,
  minWidth: "400px",
  textAlign: "left",
};

// ‚ú® Ï∂úÎ†• Ïù¥ÎØ∏ÏßÄ Ìó§Îçî ÏúÑÏπò Ï°∞Ï†ï
const outputColumnStyle: React.CSSProperties = {
  flex: 1,
  minWidth: "400px",
  textAlign: "left",
  paddingTop: "72px", // ÌîÑÎ°¨ÌîÑÌä∏ label ÎÜíÏù¥Ïóê ÎßûÏ∂§
};

const inputStyle: React.CSSProperties = {
  width: "100%",
  maxWidth: "100%",
  padding: "8px",
  marginBottom: "16px",
  borderRadius: "4px",
  border: "1px solid #ccc",
};

const dropZoneStyle: React.CSSProperties = {
  border: "2px dashed #5B5B5B",
  borderRadius: "8px",
  padding: "20px",
  backgroundColor: "#f9f9f9",
  color: "#aaa",
  textAlign: "center",
  minHeight: "300px",
  cursor: "pointer",
  transition: "border-color 0.3s",
};

const dropZoneHoverStyle: React.CSSProperties = {
  ...dropZoneStyle,
  borderColor: "#2ecc71",
  backgroundColor: "#f1fff5",
};

const downloadButtonStyle: React.CSSProperties = {
  marginTop: "20px",
  padding: "12px 24px",
  fontWeight: "bold",
  backgroundColor: "#09AA5C",
  color: "white",
  border: "none",
  borderRadius: "5px",
  cursor: "pointer",
  fontSize: "16px",
};

export default function GoodsForm() {
  const [prompt, setPrompt] = useState("Draw a realistic version of a short-sleeve white color T-shirt with this pig character printed on it.");
  const [image, setImage] = useState<File | null>(null);
  const [preview, setPreview] = useState<string>("");
  const [aspectRatio, setAspectRatio] = useState("1:1");
  const [seed, setSeed] = useState<number>(11);
  const [isRandomSeed, setIsRandomSeed] = useState(false);
  const [safetyTolerance, setSafetyTolerance] = useState(2);
  const [outputUrl, setOutputUrl] = useState("");
  const [loading, setLoading] = useState(false);
  const [dragOver, setDragOver] = useState(false);

  const handleImageUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0] || null;
    if (file) {
      setImage(file);
      setPreview(URL.createObjectURL(file));
    }
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    setDragOver(false);
    const file = e.dataTransfer.files?.[0];
    if (file) {
      setImage(file);
      setPreview(URL.createObjectURL(file));
    }
  };

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    setDragOver(true);
  };

  const handleDragLeave = (e: React.DragEvent) => {
    e.preventDefault();
    setDragOver(false);
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!image) {
      alert("Ï∫êÎ¶≠ÌÑ∞ Ïù¥ÎØ∏ÏßÄÎ•º ÏóÖÎ°úÎìúÌïòÏÑ∏Ïöî.");
      return;
    }

    const finalSeed = isRandomSeed ? Math.floor(Math.random() * 99999999) : seed;
    const formData = new FormData();
    formData.append("prompt", prompt);
    formData.append("input_image", image);
    formData.append("aspect_ratio", aspectRatio);
    formData.append("seed", finalSeed.toString());
    formData.append("safety_tolerance", safetyTolerance.toString());

    setLoading(true);
    setOutputUrl("");

    try {
      const response = await axios.post("http://localhost:8000/generate-goods", formData);
