              ) : (
                <p>Ïù¥ÎØ∏ÏßÄ ÏóÖÎ°úÎìúÏãú Ïó¨Í∏∞Ïóê ÌëúÏãúÎê©ÎãàÎã§.</p>
              )}
            </div>

<label htmlFor="aspectRatio" style={{ fontWeight: 'bold' }}>Ïù¥ÎØ∏ÏßÄ ÎπÑÏú®</label>
            <select
              id="aspectRatio"
              value={aspectRatio}
              onChange={(e) => setAspectRatio(e.target.value)}
              style={{ width: '100%', padding: '8px', marginBottom: '16px', borderRadius: '4px', border: '1px solid #ccc' }}
            >
              {aspectRatios.map(r => <option key={r} value={r}>{r}</option>)}
            </select>

            <label style={{ fontWeight: 'bold' }}>Seed ÏÑ§Ï†ï</label>
            <div style={{ display: 'flex', gap: '16px', marginBottom: '8px' }}>
              <div>
                <input type="radio" id="fixedSeed" name="seedMode" checked={!isRandomSeed} onChange={() => setIsRandomSeed(false)} />
                <label htmlFor="fixedSeed" style={{ marginLeft: '4px' }}>Í≥†Ï†ïÍ∞í ÏÇ¨Ïö©</label>
              </div>
              <div>
                <input type="radio" id="randomSeed" name="seedMode" checked={isRandomSeed} onChange={() => setIsRandomSeed(true)} />
                <label htmlFor="randomSeed" style={{ marginLeft: '4px' }}>ÎûúÎç§ÌïòÍ≤å ÏÉùÏÑ±</label>
              </div>
            </div>
            <input
              type="number"
              value={seed}
              onChange={(e) => setSeed(Number(e.target.value))}
              disabled={isRandomSeed}
              style={{ width: '100%', padding: '8px', marginBottom: '16px', borderRadius: '4px', border: '1px solid #ccc' }}
