              step={1}
              value={safetyTolerance}
              onChange={(e) => setSafetyTolerance(Number(e.target.value))}
              style={{ width: '100%', marginBottom: '16px' }}
            />

            <button type="submit" disabled={loading} style={{ ...inputStyle, backgroundColor: loading ? "#ccc" : "#09AA5C", color: "white", fontWeight: "bold", cursor: "pointer" }}>
              {loading ? "ÏÉùÏÑ± Ï§ë..." : "ÍµøÏ¶à Ï¥àÏïà ÏÉùÏÑ±‚ú®"}
            </button>
          </form>
