import React, { useState } from "react";
import axios from "axios";
import NavBar from "./NavBar.tsx"; // NavBar Ïª¥Ìè¨ÎÑåÌä∏ Í≤ΩÎ°ú ÌôïÏù∏

const aspectRatios = [
  "match_input_image", "1:1", "16:9", "9:16", "4:3", "3:4", "3:2", "2:3",
  "4:5", "5:4", "21:9", "9:21", "2:1", "1:2"
];

// --- Ïä§ÌÉÄÏùº Í∞ùÏ≤¥ Ï†ïÏùò ---
const containerStyle: React.CSSProperties = {
  fontFamily: "'Nanum Gothic', sans-serif",
  backgroundColor: "#f4f4f4",
  minHeight: "100vh",
  display: "flex",
  flexDirection: "column",
  alignItems: "center",
  padding: "20px",
  paddingTop: "80px", // NavBar ÎÜíÏù¥Î•º Í≥†Î†§Ìïú Ìå®Îî©
};

const formContainerStyle: React.CSSProperties = {
  backgroundColor: "white",
  padding: "40px",
  borderRadius: "8px",
  boxShadow: "0 4px 12px rgba(0, 0, 0, 0.08)",
  maxWidth: "720px",
  width: "100%",
  marginBottom: "30px",
};

const imageFrameStyle: React.CSSProperties = {
  border: "1px solid #ddd",
  borderRadius: "4px",
  padding: "10px",
  marginBottom: "10px",
  backgroundColor: "#f9f9f9",
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center'
};

const outputContainerStyle: React.CSSProperties = {
  backgroundColor: "white",
  padding: "30px",
  borderRadius: "8px",
  boxShadow: "0 4px 12px rgba(0, 0, 0, 0.08)",
  maxWidth: "720px",
  width: "100%",
  textAlign: "center",
};

const downloadButtonStyle: React.CSSProperties = {
  marginTop: "20px",
  padding: "12px 24px",
  fontWeight: "bold",
  backgroundColor: "#09AA5C",
  color: "white",
  border: "none",
  borderRadius: "5px",
  cursor: "pointer",
  fontSize: "16px",
  transition: "background-color 0.2s",
};

export default function GoodsForm() {
  const [prompt, setPrompt] = useState("Draw a realistic version of a short-sleeve white color T-shirt with this pig character printed on it.");
  const [image, setImage] = useState<File | null>(null);
  const [preview, setPreview] = useState<string>("");
  const [aspectRatio, setAspectRatio] = useState("1:1");
  const [seed, setSeed] = useState<number>(11);
  const [isRandomSeed, setIsRandomSeed] = useState(false);
  const [safetyTolerance, setSafetyTolerance] = useState(2);
  const [outputUrl, setOutputUrl] = useState("");
  const [loading, setLoading] = useState(false);

  const handleImageUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0] || null;
    if (file) {
      setImage(file);
      setPreview(URL.createObjectURL(file));
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!image) {
      alert("Ï∫êÎ¶≠ÌÑ∞ Ïù¥ÎØ∏ÏßÄÎ•º ÏóÖÎ°úÎìúÌïòÏÑ∏Ïöî.");
      return;
    }

    const finalSeed = isRandomSeed ? Math.floor(Math.random() * 99999999) : seed;
    const formData = new FormData();
    formData.append("prompt", prompt);
    formData.append("input_image", image);
    formData.append("aspect_ratio", aspectRatio);
    formData.append("seed", finalSeed.toString());
    formData.append("safety_tolerance", safetyTolerance.toString());

    setLoading(true);
    setOutputUrl("");

    try {
      const response = await axios.post("http://localhost:8000/generate-goods", formData);
      setOutputUrl(response.data.output_url);
    } catch (error: any) {
      const errorMessage = error.response?.data?.error || "Ïù¥ÎØ∏ÏßÄ ÏÉùÏÑ±Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.";
      alert(errorMessage);
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  const handleDownload = async () => {
    if (!outputUrl) return;
    try {
      const response = await fetch(outputUrl);
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'generated-goods.png');
      document.body.appendChild(link);
      link.click();
      link.parentNode?.removeChild(link);
      window.URL.revokeObjectURL(url);
    } catch (error) {
      console.error("Îã§Ïö¥Î°úÎìú Ïã§Ìå®:", error);
    }
  };

  return (
    <div style={containerStyle}>
      <NavBar />
      <div style={formContainerStyle}>
        <h2 style={{ textAlign: 'center', marginBottom: '30px' }}>ÏõπÌà∞ Ï∫êÎ¶≠ÌÑ∞ ÍµøÏ¶à Ï¥àÏïà ÏÉùÏÑ±</h2>
        <form onSubmit={handleSubmit}>
          <label htmlFor="prompt" style={{ display: 'block', marginBottom: '8px', fontWeight: 'bold' }}>ÌîÑÎ°¨ÌîÑÌä∏Î•º ÏòÅÏñ¥Î°ú ÏûÖÎ†•ÌïòÏÑ∏Ïöî</label>
          <textarea id="prompt" value={prompt} onChange={(e) => setPrompt(e.target.value)} style={{ width: '100%', padding: '8px', marginBottom: '16px', borderRadius: '4px', border: '1px solid #ccc', minHeight: '80px', resize: 'vertical' }} />

          <label htmlFor="imageUpload" style={{ display: 'block', marginBottom: '8px', fontWeight: 'bold' }}>ÏõπÌà∞ Ï∫êÎ¶≠ÌÑ∞ Ïù¥ÎØ∏ÏßÄÎ•º ÎÑ£Ïñ¥Ï£ºÏÑ∏Ïöî</label>
          <input id="imageUpload" type="file" accept="image/*" onChange={handleImageUpload} style={{ width: '100%', padding: '8px', marginBottom: '16px' }} />
          {preview && (
            <div style={imageFrameStyle}>
              <img src={preview} alt="preview" style={{ maxWidth: "100%", maxHeight: "300px", height: "auto" }} />
            </div>
          )}

          <label htmlFor="aspectRatio" style={{ display: 'block', marginTop: '16px', marginBottom: '8px', fontWeight: 'bold' }}>Ïù¥ÎØ∏ÏßÄ ÎπÑÏú®</label>
          <select id="aspectRatio" value={aspectRatio} onChange={(e) => setAspectRatio(e.target.value)} style={{ width: '100%', padding: '8px', marginBottom: '16px', borderRadius: '4px', border: '1px solid #ccc' }}>
            {aspectRatios.map(r => <option key={r} value={r}>{r}</option>)}
          </select>

          <label style={{ display: 'block', marginBottom: '8px', fontWeight: 'bold' }}>Seed ÏÑ§Ï†ï</label>
          <div style={{ marginBottom: '8px' }}>
            <input type="radio" id="fixedSeed" name="seedMode" checked={!isRandomSeed} onChange={() => setIsRandomSeed(false)} />
            <label htmlFor="fixedSeed" style={{ marginRight: '16px' }}>Í≥†Ï†ïÍ∞í ÏÇ¨Ïö©</label>
            <input type="radio" id="randomSeed" name="seedMode" checked={isRandomSeed} onChange={() => setIsRandomSeed(true)} />
            <label htmlFor="randomSeed">ÎûúÎç§ÌïòÍ≤å ÏÉùÏÑ±</label>
          </div>
          <input type="number" value={seed} onChange={(e) => setSeed(Number(e.target.value))} disabled={isRandomSeed} style={{ width: '100%', padding: '8px', marginBottom: '16px', borderRadius: '4px', border: '1px solid #ccc' }} />

          <label htmlFor="safetyTolerance" style={{ display: 'block', marginBottom: '8px', fontWeight: 'bold' }}>Safety Tolerance: {safetyTolerance}</label>
          <input id="safetyTolerance" type="range" min={0} max={2} step={1} value={safetyTolerance} onChange={(e) => setSafetyTolerance(Number(e.target.value))} style={{ width: '100%', marginBottom: '16px' }} />

          <button type="submit" disabled={loading} style={{ width: '100%', padding: '12px', fontSize: '16px', cursor: 'pointer', backgroundColor: loading ? '#ccc' : '#09AA5C', color: 'white', border: 'none', borderRadius: '5px', fontWeight: 'bold' }}>
            {loading ? "ÏÉùÏÑ± Ï§ë..." : "ÍµøÏ¶à Ï¥àÏïà ÏÉùÏÑ±‚ú®"}
          </button>
