import React, { useState } from "react";
import axios from "axios";
import NavBar from "./NavBar.tsx";

const aspectRatios = [
  "match_input_image", "1:1", "16:9", "9:16", "4:3", "3:4",
  "3:2", "2:3", "4:5", "5:4", "21:9", "9:21", "2:1", "1:2"
];

export default function GoodsForm() {
  // PPL 생성기와 상태 변수 통일
  const [prompt, setPrompt] = useState("Draw a realistic version of a short-sleeve white color T-shirt with this pig character printed on it.");
  const [image1, setImage1] = useState<File | null>(null); // 캐릭터 이미지
  const [image2, setImage2] = useState<File | null>(null); // 제품 이미지
  const [preview1, setPreview1] = useState<string>("");
  const [preview2, setPreview2] = useState<string>("");
  const [aspectRatio, setAspectRatio] = useState("1:1"); // 기본값을 1:1로 변경
  const [seed, setSeed] = useState<number | null>(null); // PPL처럼 null 허용
  const [outputFormat, setOutputFormat] = useState("png"); // PPL처럼 outputFormat 추가
  const [safetyTolerance, setSafetyTolerance] = useState(2);
  const [outputUrl, setOutputUrl] = useState("");
  const [loading, setLoading] = useState(false);

  // PPL과 동일한 이미지 업로드 핸들러
  const handleImageUpload = (file: File | null, setImage: React.Dispatch<React.SetStateAction<File | null>>, setPreview: React.Dispatch<React.SetStateAction<string>>) => {
    if (file) {
      setImage(file);
      setPreview(URL.createObjectURL(file));
    }
  };

  // PPL과 동일한 랜덤 시드 핸들러
  const handleRandomSeed = () => {
    const randomSeed = Math.floor(Math.random() * 999999);
    setSeed(randomSeed);
  };

  // PPL과 동일한 다운로드 핸들러
  const handleDownload = async () => {
    if (!outputUrl) return;
    try {
      const response = await fetch(outputUrl);
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'generated-goods.png');
      document.body.appendChild(link);
      link.click();
      link.parentNode?.removeChild(link);
      window.URL.revokeObjectURL(url);
    } catch (error) {
      console.error("이미지 다운로드에 실패했습니다.", error);
    }
  };
  
  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    // PPL처럼 이미지 2개를 확인
    if (!image1 || !image2) {
      alert("이미지 두 개를 모두 업로드하세요.");
      return;
    }

    const formData = new FormData();
    formData.append("prompt", prompt);
    formData.append("input_image_1", image1); // PPL처럼 image1 전송
    formData.append("input_image_2", image2); // PPL처럼 image2 전송
    formData.append("aspect_ratio", aspectRatio);
    formData.append("output_format", outputFormat); // PPL처럼 outputFormat 전송
    formData.append("safety_tolerance", safetyTolerance.toString());
    if (seed !== null) {
      formData.append("seed", seed.toString());
    }

    setLoading(true);
    setOutputUrl("");

    try {
      // ✅ 중요: 백엔드 엔드포인트는 그대로 /generate-goods 사용
      const response = await axios.post<{ output_url: string }>("http://localhost:8000/generate-goods", formData);
      setOutputUrl(response.data.output_url);
    } catch (error: any) {
      const errorMessage = error.response?.data?.error || "이미지 생성에 실패했습니다.";
      alert(errorMessage);
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ fontFamily: "NanumGothic" }}>
      <NavBar />

      <div style={{ display: "flex", padding: 40, maxWidth: 1440, margin: "0 auto" }}>
        {/* Left Input Panel */}
        <form onSubmit={handleSubmit} style={{ flex: 1, paddingRight: 20 }}>
          <h2 style={{ fontFamily: 'NanumGothic', fontSize: 28, fontWeight: 700, color: '#000' }}>
            웹툰 캐릭터 굿즈 초안 생성
          </h2>

          {/* PPL처럼 input으로 변경하고 스타일 적용 */}
          <label htmlFor="prompt" style={{ display: 'block', color: '#000', fontWeight: 700, fontSize: 18 }}>프롬프트를 영어로 입력하세요</label>
          <input
            id="prompt"
            value={prompt}
            onChange={(e) => setPrompt(e.target.value)}
            style={{ width: '100%', padding: 8, marginTop: 8, marginBottom: 16 }}
          />

          {/* PPL처럼 이미지 입력 2개로 변경 */}
          <label style={{ color: '#000', fontWeight: 700, fontSize: 18 }}>① 캐릭터 이미지</label>
          <input
            type="file"
            accept="image/*"
            onChange={(e) => handleImageUpload(e.target.files?.[0] || null, setImage1, setPreview1)}
            style={{ width: '100%' }}
          />
          {preview1 && <img src={preview1} alt="캐릭터 이미지 미리보기" style={{ width: "100%", marginTop: 8 }} />}

          <label style={{ color: '#000', fontWeight: 700, fontSize: 18, display: "block", marginTop: 16 }}>② 제품 이미지 (티셔츠, 머그컵 등)</label>
          <input
            type="file"
            accept="image/*"
            onChange={(e) => handleImageUpload(e.target.files?.[0] || null, setImage2, setPreview2)}
            style={{ width: '100%' }}
          />
          {preview2 && <img src={preview2} alt="제품 이미지 미리보기" style={{ width: "100%", marginTop: 8 }} />}

          <label style={{ color: '#000', fontWeight: 700, fontSize: 18, display: "block", marginTop: 16 }}>비율</label>
          <select
            value={aspectRatio}
            onChange={(e) => setAspectRatio(e.target.value)}
            style={{ width: '100%', padding: 8, marginTop: 8, marginBottom: 8 }}
          >
            {aspectRatios.map((r) => <option key={r} value={r}>{r}</option>)}
          </select>
          
          {/* PPL처럼 Seed 입력 방식으로 변경 */}
          <label style={{ color: '#000', fontWeight: 700, fontSize: 18, display: 'block', marginTop: 8 }}>Seed</label>
          <div style={{ display: 'flex', gap: 8, marginTop: 8 }}>
            <input
              type="number"
              value={seed === null ? '' : seed}
              onChange={(e) => setSeed(e.target.value === '' ? null : Number(e.target.value))}
              placeholder="랜덤이거나 입력하기"
              style={{ flex: 2, padding: '8px', fontSize: 16, height: 42, marginBottom: 8 }}
            />
            <button type="button" onClick={handleRandomSeed} style={{ flex: 1, backgroundColor: '#09AA5C', color: '#fff', fontWeight: 'bold', border: 'none' }}>
              랜덤
            </button>
          </div>

          {/* PPL처럼 출력 형식 추가 */}
          <label style={{ color: '#000', fontWeight: 700, fontSize: 18, display: 'block', marginTop: 8 }}>출력 형식</label>
          <select
            value={outputFormat}
            onChange={(e) => setOutputFormat(e.target.value)}
            style={{ width: '100%', padding: 8, marginTop: 8, marginBottom: 8 }}
          >
            <option value="png">png</option>
            <option value="jpeg">jpeg</option>
          </select>

          <label style={{ color: '#000', fontWeight: 700, fontSize: 18, display: 'block', marginTop: 8 }}>
            안전성 수치: {safetyTolerance}
          </label>
          <input
            type="range"
            min={0}
            max={2}
            step={1}
            value={safetyTolerance}
            onChange={(e) => setSafetyTolerance(Number(e.target.value))}
            style={{ width: '100%', marginTop: 8 }}
          />

          <button type="submit" disabled={loading} style={{ width: '100%', padding: 12, marginTop: 20, backgroundColor: loading ? '#ccc' : '#09AA5C', color: 'white', fontWeight: 'bold', fontSize: 16, border: 'none', borderRadius: 5, cursor: loading ? 'not-allowed' : 'pointer' }}>
            {loading ? "생성 중..." : "굿즈 초안 생성✨"}
          </button>
        </form>
        
        {/* Right Output Panel */}
        <div style={{ flex: 1, paddingLeft: 20 }}>
          <h3>Output</h3>
          {loading && <p>이미지를 생성 중입니다...</p>}
          {!loading && outputUrl ? (
            <>
              <img src={outputUrl} alt="결과 이미지" style={{ width: "100%", borderRadius: 8 }} />
              <button onClick={handleDownload} style={{ marginTop: 10, padding: 10, fontWeight: "bold", backgroundColor: "#09AA5C", color: "white", border: "none", borderRadius: 5, cursor: "pointer" }}>
                📥 다운로드
              </button>
            </>
          ) : (
            !loading && <p style={{ color: '#5B5B5B' }}>이미지가 생성되면 이곳에 표시됩니다.</p>
          )}
        </div>
      </div>
    </div>
  );
}
