import React, { useState } from "react";
import axios from "axios";
import NavBar from "./NavBar.tsx";

const aspectRatios = [
  "match_input_image", "1:1", "16:9", "9:16", "4:3", "3:4", "3:2", "2:3",
  "4:5", "5:4", "21:9", "9:21", "2:1", "1:2"
];

const containerStyle: React.CSSProperties = {
  fontFamily: "'Nanum Gothic', sans-serif",
  backgroundColor: "#ffffff",
  minHeight: "100vh",
  margin: 0,
  padding: 0,
  overflowX: "hidden",
};

const mainContentStyle: React.CSSProperties = {
  display: "flex",
  gap: "40px",
  maxWidth: "1440px",
  margin: "0 auto",
  padding: "0 20px",
  alignItems: "flex-start",
  boxSizing: "border-box",
};

const formColumnStyle: React.CSSProperties = {
  flex: 1,
  minWidth: "400px",
  textAlign: "left",
};

const outputColumnStyle: React.CSSProperties = {
  flex: 1,
  minWidth: "400px",
  textAlign: "left",
  paddingTop: "72px",
};

const fileInputBoxStyle: React.CSSProperties = {
  width: '100%',
  padding: '8px',
  marginBottom: '16px',
  marginTop: '8px',
  border: '1px solid #ccc',
  borderRadius: '4px',
};

const imagePreviewFrameStyle: React.CSSProperties = {
  border: "1px solid #ddd",
  borderRadius: "8px",
  padding: "10px",
  marginTop: "8px",
  marginBottom: "24px",
  backgroundColor: "#f9f9f9",
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
  minHeight: "300px",
  width: "100%",
  color: "#aaa",
  fontSize: "14px",
};

const promptBoxStyle: React.CSSProperties = {
  width: '100%',
  padding: '8px',
  marginBottom: '16px',
  borderRadius: '4px',
  border: '1px solid #ccc',
  minHeight: '40px',
  resize: 'vertical'
};

const generateButtonStyle: React.CSSProperties = {
  width: '100%',
  height: '56px',
  padding: '14px',
  fontSize: '18px',
  fontWeight: 'bold',
  backgroundColor: '#09AA5C',
  color: 'white',
  border: 'none',
  borderRadius: '5px',
  cursor: 'pointer',
  marginTop: '20px',
};

const downloadButtonStyle: React.CSSProperties = {
  width: '100%',
  height: '56px',
  padding: '14px',
  fontSize: '18px',
  fontWeight: 'bold',
  backgroundColor: '#09AA5C',
  color: 'white',
  border: 'none',
  borderRadius: '5px',
  cursor: 'pointer',
  marginTop: '20px',
};

export default function GoodsForm() {
  const [prompt, setPrompt] = useState("Draw a realistic version of a short-sleeve white color T-shirt with this pig character printed on it.");
  const [image, setImage] = useState<File | null>(null);
  const [preview, setPreview] = useState<string>("");
  const [aspectRatio, setAspectRatio] = useState("1:1");
  const [seed, setSeed] = useState<number>(11);
  const [isRandomSeed, setIsRandomSeed] = useState(false);
  const [safetyTolerance, setSafetyTolerance] = useState(2);
  const [outputUrl, setOutputUrl] = useState("");
  const [loading, setLoading] = useState(false);

  const handleImageUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0] || null;
    if (file) {
      setImage(file);
      setPreview(URL.createObjectURL(file));
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!image) {
      alert("Ï∫êÎ¶≠ÌÑ∞ Ïù¥ÎØ∏ÏßÄÎ•º ÏóÖÎ°úÎìúÌïòÏÑ∏Ïöî.");
      return;
    }

    const finalSeed = isRandomSeed ? Math.floor(Math.random() * 99999999) : seed;
    const formData = new FormData();
    formData.append("prompt", prompt);
    formData.append("input_image", image);
    formData.append("aspect_ratio", aspectRatio);
    formData.append("seed", finalSeed.toString());
    formData.append("safety_tolerance", safetyTolerance.toString());

    setLoading(true);
    setOutputUrl("");

    try {
      // ‚úÖ [ÌïµÏã¨ ÏàòÏ†ï] 1. ÏùëÎãµ ÌÉÄÏûÖÏùÑ 'blob'ÏúºÎ°ú ÏßÄÏ†ïÌïòÏó¨ Ïù¥ÎØ∏ÏßÄ Îç∞Ïù¥ÌÑ∞Î•º ÏßÅÏ†ë Î∞õÏäµÎãàÎã§.
      const response = await axios.post("/api/goods-gen/generate", formData, {
        responseType: 'blob',
      });

      // ‚úÖ [ÌïµÏã¨ ÏàòÏ†ï] 2. Î∞õÏùÄ blob Îç∞Ïù¥ÌÑ∞Î°ú Î∏åÎùºÏö∞Ï†ÄÏóêÏÑú ÏÇ¨Ïö©Ìï† Ïàò ÏûàÎäî ÏûÑÏãú URLÏùÑ ÏÉùÏÑ±Ìï©ÎãàÎã§.
      const imageUrl = URL.createObjectURL(response.data);
      setOutputUrl(imageUrl);

    } catch (error: any) {
      const errorMessage = error.response?.data?.error || "Ïù¥ÎØ∏ÏßÄ ÏÉùÏÑ±Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.";
      alert(errorMessage);
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  // ‚úÖ [ÏàòÏ†ï] Îã§Ïö¥Î°úÎìú Ìï∏Îì§Îü¨Î•º Îçî Í∞ÑÎã®ÌïòÍ≤å Î≥ÄÍ≤Ω
  const handleDownload = () => {
    if (!outputUrl) return;
    const link = document.createElement("a");
    link.href = outputUrl;
    link.setAttribute("download", "generated-goods.png");
    document.body.appendChild(link);
    link.click();
    link.parentNode?.removeChild(link);
  };

  return (
    <div style={containerStyle}>
      <NavBar />
      <div style={mainContentStyle}>
        <div style={formColumnStyle}>
          <h2 style={{ marginBottom: "30px" }}>ÏõπÌà∞ Ï∫êÎ¶≠ÌÑ∞ ÍµøÏ¶à Ï¥àÏïà ÏÉùÏÑ±</h2>
          <form onSubmit={handleSubmit}>
            <label htmlFor="prompt" style={{ fontWeight: "bold", display: 'block', marginBottom: '6px' }}>
              ÌîÑÎ°¨ÌîÑÌä∏Î•º ÏòÅÏñ¥Î°ú ÏûÖÎ†•ÌïòÏÑ∏Ïöî
            </label>
            <textarea
              id="prompt"
              value={prompt}
              onChange={(e) => setPrompt(e.target.value)}
              style={promptBoxStyle}
            />

            <label htmlFor="imageUpload" style={{ fontWeight: "bold", display: 'block', marginBottom: '8px' }}>
              ÏõπÌà∞ Ï∫êÎ¶≠ÌÑ∞ Ïù¥ÎØ∏ÏßÄÎ•º ÎÑ£Ïñ¥Ï£ºÏÑ∏Ïöî
            </label>
            <input
              id="imageUpload"
              type="file"
              accept="image/*"
              onChange={handleImageUpload}
              style={fileInputBoxStyle}
            />

            <div style={imagePreviewFrameStyle}>
              {preview ? (
                <img src={preview} alt="Preview" style={{ maxWidth: "100%", maxHeight: "300px", height: "auto" }} />
              ) : (
                <p>Ïù¥ÎØ∏ÏßÄ ÏóÖÎ°úÎìú Ïãú Ïó¨Í∏∞Ïóê ÌëúÏãúÎê©ÎãàÎã§.</p>
              )}
            </div>

            <label htmlFor="aspectRatio" style={{ fontWeight: "bold", marginBottom: '16px' }}>Ïù¥ÎØ∏ÏßÄ ÎπÑÏú®</label>
            <select
              id="aspectRatio"
              value={aspectRatio}
              onChange={(e) => setAspectRatio(e.target.value)}
              style={fileInputBoxStyle}
            >
              {aspectRatios.map((r) => (
                <option key={r} value={r}>{r}</option>
              ))}
            </select>

            <label style={{ fontWeight: "bold", marginTop: "20px" }}>Seed ÏÑ§Ï†ï</label>
            <div style={{ display: "flex", gap: "16px", marginTop: "8px" }}>
              <div>
                <input type="radio" id="fixedSeed" name="seedMode" checked={!isRandomSeed} onChange={() => setIsRandomSeed(false)} />
                <label htmlFor="fixedSeed" style={{ marginLeft: "4px",}}>Í≥†Ï†ïÍ∞í ÏÇ¨Ïö©</label>
              </div>
              <div>
                <input type="radio" id="randomSeed" name="seedMode" checked={isRandomSeed} onChange={() => setIsRandomSeed(true)} />
                <label htmlFor="randomSeed" style={{ marginLeft: "4px",}}>ÎûúÎç§ÌïòÍ≤å ÏÉùÏÑ±</label>
              </div>
            </div>
            <input
              type="number"
              value={seed}
              onChange={(e) => setSeed(Number(e.target.value))}
              disabled={isRandomSeed}
              style={fileInputBoxStyle}
            />

            <label htmlFor="safetyTolerance" style={{ fontWeight: "bold",}}>Safety Tolerance(ÌîÑÎ°¨ÌîÑÌä∏ Ï†ÅÏö©): {safetyTolerance}</label>
            <input
              id="safetyTolerance"
              type="range"
              min={0}
              max={2}
              step={1}
              value={safetyTolerance}
              onChange={(e) => setSafetyTolerance(Number(e.target.value))}
              style={{ width: '100%', marginTop: '8px', marginBottom: '16px' }}
            />

            <button
              type="submit"
              disabled={loading}
              style={{
                ...generateButtonStyle,
                backgroundColor: loading ? "#ccc" : "#09AA5C"
              }}
            >
              {loading ? "ÏÉùÏÑ± Ï§ë..." : "ÍµøÏ¶à Ï¥àÏïà ÏÉùÏÑ±‚ú®"}
            </button>
          </form>
        </div>

        <div style={outputColumnStyle}>
          <h3 style={{ marginBottom: "10px" }}>Ï∂úÎ†• Ïù¥ÎØ∏ÏßÄ</h3>
          <div style={imagePreviewFrameStyle}>
            {loading && <p>Ïù¥ÎØ∏ÏßÄÎ•º ÏÉùÏÑ± Ï§ëÏûÖÎãàÎã§...</p>}
            {!loading && outputUrl ? (
              <img src={outputUrl} alt="Generated" style={{ maxWidth: "100%", maxHeight: "400px", height: "auto" }} />
            ) : (
              !loading && <p style={{ color: "#aaa" }}>ÏÉùÏÑ±Îêú Ïù¥ÎØ∏ÏßÄÍ∞Ä Ïó¨Í∏∞Ïóê ÌëúÏãúÎê©ÎãàÎã§.</p>
            )}
          </div>
          {outputUrl && !loading && (
            <button onClick={handleDownload} style={downloadButtonStyle}>
              üì• Ïù¥ÎØ∏ÏßÄ Îã§Ïö¥Î°úÎìú
            </button>
          )}
        </div>
      </div>
    </div>
  );
}
