import React, { useState } from "react";
import axios from "axios";
import NavBar from "./NavBar.tsx";

const aspectRatios = [
  "match_input_image", "1:1", "16:9", "9:16", "4:3", "3:4", "3:2", "2:3",
  "4:5", "5:4", "21:9", "9:21", "2:1", "1:2"
];

const containerStyle: React.CSSProperties = {
  fontFamily: "'Nanum Gothic', sans-serif",
  backgroundColor: "#ffffff",
  minHeight: "100vh",
  margin: 0,
  padding: 0,
  overflowX: "hidden",
};

// ‚úÖ [ÏàòÏ†ï] flexWrap: 'wrap'ÏùÑ Ï∂îÍ∞ÄÌïòÏó¨ ÌôîÎ©¥Ïù¥ Ï¢ÅÏùÑ Îïå Ïª¨ÎüºÏù¥ ÏÑ∏Î°úÎ°ú ÏåìÏù¥ÎèÑÎ°ù Ìï©ÎãàÎã§.
const mainContentStyle: React.CSSProperties = {
  display: "flex",
  flexWrap: "wrap",
  gap: "40px",
  maxWidth: "1440px",
  margin: "0 auto",
  padding: "20px", // ÏùºÍ¥ÄÏÑ±ÏùÑ ÏúÑÌï¥ padding ÏàòÏ†ï
  alignItems: "flex-start",
  boxSizing: "border-box",
};

const formColumnStyle: React.CSSProperties = {
  flex: "1 1 400px", // ‚úÖ [ÏàòÏ†ï] flex ÏïÑÏù¥ÌÖúÏù¥ Ïú†Ïó∞ÌïòÍ≤å Ï§ÑÏñ¥Îì§ Ïàò ÏûàÎèÑÎ°ù Î≥ÄÍ≤Ω
  minWidth: "320px",  // ‚úÖ [ÏàòÏ†ï] ÏµúÏÜå ÎÑàÎπÑÎ•º Ï§ÑÏó¨ ÏûëÏùÄ ÌôîÎ©¥Ïóê ÎåÄÏùë
  textAlign: "left",
};

const outputColumnStyle: React.CSSProperties = {
  flex: "1 1 400px", // ‚úÖ [ÏàòÏ†ï] flex ÏïÑÏù¥ÌÖúÏù¥ Ïú†Ïó∞ÌïòÍ≤å Ï§ÑÏñ¥Îì§ Ïàò ÏûàÎèÑÎ°ù Î≥ÄÍ≤Ω
  minWidth: "320px",  // ‚úÖ [ÏàòÏ†ï] ÏµúÏÜå ÎÑàÎπÑÎ•º Ï§ÑÏó¨ ÏûëÏùÄ ÌôîÎ©¥Ïóê ÎåÄÏùë
  textAlign: "left",
  // ‚úÖ [ÏàòÏ†ï] Î™®Î∞îÏùºÏóêÏÑú ÏÑ∏Î°úÎ°ú ÏåìÏùº ÎïåÎ•º ÎåÄÎπÑÌï¥ ÏÉÅÎã® Ìå®Îî© Ï°∞Ï†ï
  paddingTop: "20px",
};

const fileInputBoxStyle: React.CSSProperties = {
  width: '100%',
  padding: '8px',
  marginBottom: '16px',
  marginTop: '8px',
  border: '1px solid #ccc',
  borderRadius: '4px',
  boxSizing: 'border-box', // ‚úÖ [Ï∂îÍ∞Ä] Ìå®Îî©Ïù¥ ÎÑàÎπÑÏóê Ìè¨Ìï®ÎêòÎèÑÎ°ù ÏÑ§Ï†ï
};

const imagePreviewFrameStyle: React.CSSProperties = {
  border: "1px solid #ddd",
  borderRadius: "8px",
  padding: "10px",
  marginTop: "8px",
  marginBottom: "24px",
  backgroundColor: "#f9f9f9",
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
  minHeight: "300px",
  width: "100%",
  color: "#aaa",
  fontSize: "14px",
  boxSizing: 'border-box',
};

const promptBoxStyle: React.CSSProperties = {
  width: '100%',
  padding: '8px',
  marginBottom: '16px',
  borderRadius: '4px',
  border: '1px solid #ccc',
  minHeight: '40px',
  resize: 'vertical',
  boxSizing: 'border-box',
};

const generateButtonStyle: React.CSSProperties = {
  width: '100%',
  height: '56px',
  padding: '14px',
  fontSize: '18px',
  fontWeight: 'bold',
  backgroundColor: '#09AA5C',
  color: 'white',
  border: 'none',
  borderRadius: '5px',
  cursor: 'pointer',
  marginTop: '20px',
};

const downloadButtonStyle: React.CSSProperties = {
  width: '100%',
  height: '56px',
  fontSize: '18px',
  fontWeight: 'bold',
  backgroundColor: '#09AA5C',
  color: 'white',
  border: 'none',
  borderRadius: '5px',
  cursor: 'pointer',
  marginTop: '20px',
};

export default function GenerateForm() {
  const [prompt, setPrompt] = useState("Draw the woman in the picture walking with this handbag.");
  const [image1, setImage1] = useState<File | null>(null);
  const [image2, setImage2] = useState<File | null>(null);
  const [preview1, setPreview1] = useState<string>("");
  const [preview2, setPreview2] = useState<string>("");
  const [aspectRatio, setAspectRatio] = useState("1:1");
  const [outputFormat, setOutputFormat] = useState("png");
  const [outputUrl, setOutputUrl] = useState("");
  const [loading, setLoading] = useState(false);
  const [seed, setSeed] = useState<number>(11);
  const [isRandomSeed, setIsRandomSeed] = useState(false);
  const [safetyTolerance, setSafetyTolerance] = useState(2);

  const handleImageUpload = (
    file: File | null,
    setImage: React.Dispatch<React.SetStateAction<File | null>>,
    setPreview: React.Dispatch<React.SetStateAction<string>>
  ) => {
    if (file) {
      setImage(file);
      setPreview(URL.createObjectURL(file));
    }
  };

  const handleDownload = () => {
    if (!outputUrl) return;
    const link = document.createElement('a');
    link.href = outputUrl;
    link.setAttribute('download', `generated-ppl-image.${outputFormat}`);
    document.body.appendChild(link);
    link.click();
    link.parentNode?.removeChild(link);
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!image1 || !image2) {
      alert("Ïù¥ÎØ∏ÏßÄ Îëê Í∞úÎ•º Î™®Îëê ÏóÖÎ°úÎìúÌïòÏÑ∏Ïöî.");
      return;
    }

    const finalSeed = isRandomSeed ? Math.floor(Math.random() * 999999) : seed;

    const formData = new FormData();
    formData.append("prompt", prompt);
    formData.append("input_image_1", image1);
    formData.append("input_image_2", image2);
    formData.append("aspect_ratio", aspectRatio);
    formData.append("output_format", outputFormat);
    formData.append("safety_tolerance", safetyTolerance.toString());
    formData.append("seed", finalSeed.toString());

    setLoading(true);
    setOutputUrl("");

    try {
      const response = await axios.post("/api/ppl-gen/generate", formData, {
        responseType: 'blob',
      });

      const imageUrl = URL.createObjectURL(response.data);
      setOutputUrl(imageUrl);

    } catch (error) {
      alert("Ïù¥ÎØ∏ÏßÄ ÏÉùÏÑ±Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§. ÏûÖÎ†• Í∞íÏù¥ÎÇò Î™®Îç∏ ÏÉÅÌÉúÎ•º ÌôïÏù∏Ìï¥ Ï£ºÏÑ∏Ïöî.");
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={containerStyle}>
      <NavBar />
      <div style={mainContentStyle}>
        <div style={formColumnStyle}>
          <h2 style={{ marginBottom: '30px' }}>ÏõπÌà∞ Ï∫êÎ¶≠ÌÑ∞ PPL ÏÉùÏÑ±Í∏∞</h2>
          <form onSubmit={handleSubmit}>
            <label htmlFor="prompt" style={{ fontWeight: "bold", display: 'block', marginBottom: '6px' }}>ÌîÑÎ°¨ÌîÑÌä∏Î•º ÏòÅÏñ¥Î°ú ÏûÖÎ†•ÌïòÏÑ∏Ïöî</label>
            <textarea
              id="prompt"
              value={prompt}
              onChange={(e) => setPrompt(e.target.value)}
              style={promptBoxStyle}
            />

            <label style={{ fontWeight: "bold", display: 'block', marginBottom: '8px' }}>‚ë† Ï∫êÎ¶≠ÌÑ∞ Ïù¥ÎØ∏ÏßÄ</label>
            <input type="file" accept="image/*" onChange={(e) => handleImageUpload(e.target.files?.[0] || null, setImage1, setPreview1)} style={fileInputBoxStyle} />
            <div style={imagePreviewFrameStyle}>
              {preview1 ? (
                <img src={preview1} alt="Ï∫êÎ¶≠ÌÑ∞ Ïù¥ÎØ∏ÏßÄ" style={{ maxWidth: "100%", maxHeight: "300px" }} />
              ) : (
                <p>Ïù¥ÎØ∏ÏßÄ ÏóÖÎ°úÎìú Ïãú Ïó¨Í∏∞Ïóê ÌëúÏãúÎê©ÎãàÎã§.</p>
              )}
            </div>

            <label style={{ fontWeight: "bold", display: 'block', marginBottom: '8px' }}>‚ë° Ï†úÌíà Ïù¥ÎØ∏ÏßÄ</label>
            <input type="file" accept="image/*" onChange={(e) => handleImageUpload(e.target.files?.[0] || null, setImage2, setPreview2)} style={fileInputBoxStyle} />
            <div style={imagePreviewFrameStyle}>
              {preview2 ? (
                <img src={preview2} alt="Ï†úÌíà Ïù¥ÎØ∏ÏßÄ" style={{ maxWidth: "100%", maxHeight: "300px" }} />
              ) : (
                <p>Ïù¥ÎØ∏ÏßÄ ÏóÖÎ°úÎìú Ïãú Ïó¨Í∏∞Ïóê ÌëúÏãúÎê©ÎãàÎã§.</p>
              )}
            </div>

            <label style={{ fontWeight: "bold", display: 'block', marginBottom: '8px' }}>Ïù¥ÎØ∏ÏßÄ ÎπÑÏú®</label>
            <select value={aspectRatio} onChange={(e) => setAspectRatio(e.target.value)} style={fileInputBoxStyle}>
              {aspectRatios.map(r => <option key={r} value={r}>{r}</option>)}
            </select>

            <label style={{ fontWeight: "bold", display: 'block', marginBottom: '8px' }}>Ï∂úÎ†• ÌòïÏãù</label>
            <select value={outputFormat} onChange={(e) => setOutputFormat(e.target.value)} style={fileInputBoxStyle}>
              <option value="png">png</option>
              <option value="jpeg">jpeg</option>
            </select>

            <label style={{ fontWeight: "bold", display: 'block', marginTop: "20px" }}>Seed ÏÑ§Ï†ï</label>
            <div style={{ display: "flex", gap: "16px", marginTop: "8px" }}>
              <div>
                <input type="radio" id="fixedSeed" name="seedMode" checked={!isRandomSeed} onChange={() => setIsRandomSeed(false)} />
                <label htmlFor="fixedSeed" style={{ marginLeft: "4px" }}>Í≥†Ï†ïÍ∞í ÏÇ¨Ïö©</label>
              </div>
              <div>
                <input type="radio" id="randomSeed" name="seedMode" checked={isRandomSeed} onChange={() => setIsRandomSeed(true)} />
                <label htmlFor="randomSeed" style={{ marginLeft: "4px" }}>ÎûúÎç§ÌïòÍ≤å ÏÉùÏÑ±</label>
              </div>
            </div>
            <input type="number" value={seed} onChange={(e) => setSeed(Number(e.target.value))} disabled={isRandomSeed} style={fileInputBoxStyle} />

            <label htmlFor="safetyTolerance" style={{ fontWeight: "bold" }}>Safety Tolerance(ÌîÑÎ°¨ÌîÑÌä∏ Ï†ÅÏö©): {safetyTolerance}</label>
            <input id="safetyTolerance" type="range" min={0} max={2} step={1} value={safetyTolerance} onChange={(e) => setSafetyTolerance(Number(e.target.value))} style={{ width: '100%', marginTop: '8px', marginBottom: '16px' }} />

            <button type="submit" disabled={loading} style={{ ...generateButtonStyle, backgroundColor: loading ? "#ccc" : "#09AA5C" }}>
              {loading ? "ÏÉùÏÑ± Ï§ë..." : "PPL Ïù¥ÎØ∏ÏßÄ ÏÉùÏÑ±‚ú®"}
            </button>
          </form>
        </div>

        <div style={outputColumnStyle}>
          <h3 style={{ marginBottom: "10px" }}>Ï∂úÎ†• Ïù¥ÎØ∏ÏßÄ</h3>
          <div style={imagePreviewFrameStyle}>
            {loading && <p>Ïù¥ÎØ∏ÏßÄÎ•º ÏÉùÏÑ± Ï§ëÏûÖÎãàÎã§...</p>}
            {!loading && outputUrl ? (
              <img src={outputUrl} alt="Ï∂úÎ†• Ïù¥ÎØ∏ÏßÄ" style={{ maxWidth: "100%", maxHeight: "400px", height: "auto" }} />
            ) : (
              !loading && <p style={{ color: "#aaa" }}>ÏÉùÏÑ±Îêú Ïù¥ÎØ∏ÏßÄÍ∞Ä Ïó¨Í∏∞Ïóê ÌëúÏãúÎê©ÎãàÎã§.</p>
            )}
          </div>
          {outputUrl && !loading && (
            <button onClick={handleDownload} style={downloadButtonStyle}>
              üì• Ïù¥ÎØ∏ÏßÄ Îã§Ïö¥Î°úÎìú
            </button>
          )}
        </div>
      </div>
    </div>
  );
}
