import React, { useState } from "react";
import axios from "axios";
import NavBar from "./NavBar.tsx";

const aspectRatios = [
  "match_input_image", "1:1", "16:9", "9:16", "4:3", "3:4", "3:2", "2:3",
  "4:5", "5:4", "21:9", "9:21", "2:1", "1:2"
];

const containerStyle: React.CSSProperties = {
  fontFamily: "'Nanum Gothic', sans-serif",
  backgroundColor: "#ffffff",
  minHeight: "100vh",
  margin: 0,
  padding: 0,
  overflowX: "hidden",
};

const mainContentStyle: React.CSSProperties = {
  display: "flex",
  gap: "40px",
  maxWidth: "1440px",
  margin: "0 auto",
  padding: "0 20px",
  alignItems: "flex-start",
  boxSizing: "border-box",
};

const formColumnStyle: React.CSSProperties = {
  flex: 1,
  minWidth: "400px",
  textAlign: "left",
};

const outputColumnStyle: React.CSSProperties = {
  flex: 1,
  minWidth: "400px",
  textAlign: "left",
  paddingTop: "72px",
};

const fileInputBoxStyle: React.CSSProperties = {
  width: '100%',
  padding: '8px',
  marginBottom: '16px',
  marginTop: '8px',
  border: '1px solid #ccc',
  borderRadius: '4px',
};

const imagePreviewFrameStyle: React.CSSProperties = {
  border: "1px solid #ddd",
  borderRadius: "8px",
  padding: "10px",
  marginTop: "8px",
  marginBottom: "24px",
  backgroundColor: "#f9f9f9",
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
  minHeight: "300px",
  width: "100%",
  color: "#aaa",
  fontSize: "14px",
};

const promptBoxStyle: React.CSSProperties = {
  width: '100%',
  padding: '8px',
  marginBottom: '16px',
  borderRadius: '4px',
  border: '1px solid #ccc',
  minHeight: '40px',
  resize: 'vertical'
};

const generateButtonStyle: React.CSSProperties = {
  width: '100%',
  height: '56px',
  padding: '14px',
  fontSize: '18px',
  fontWeight: 'bold',
  backgroundColor: '#09AA5C',
  color: 'white',
  border: 'none',
  borderRadius: '5px',
  cursor: 'pointer',
  marginTop: '20px',
};

const downloadButtonStyle: React.CSSProperties = {
  width: '100%',
  height: '56px',
  fontSize: '18px',
  fontWeight: 'bold',
  backgroundColor: '#09AA5C',
  color: 'white',
  border: 'none',
  borderRadius: '5px',
  cursor: 'pointer',
  marginTop: '20px',
};

export default function GenerateForm() {
  const [prompt, setPrompt] = useState("Draw the woman in the picture walking with this handbag.");
  const [image1, setImage1] = useState<File | null>(null);
  const [image2, setImage2] = useState<File | null>(null);
  const [preview1, setPreview1] = useState<string>("");
  const [preview2, setPreview2] = useState<string>("");
  const [aspectRatio, setAspectRatio] = useState("1:1");
  const [outputFormat, setOutputFormat] = useState("png");
  const [outputUrl, setOutputUrl] = useState("");
  const [loading, setLoading] = useState(false);
  const [seed, setSeed] = useState<number>(11);
  const [isRandomSeed, setIsRandomSeed] = useState(false);
  const [safetyTolerance, setSafetyTolerance] = useState(2);

  const handleImageUpload = (
    file: File | null,
    setImage: React.Dispatch<React.SetStateAction<File | null>>,
    setPreview: React.Dispatch<React.SetStateAction<string>>
  ) => {
    if (file) {
      setImage(file);
      setPreview(URL.createObjectURL(file));
    }
  };

  const handleDownload = async () => {
    if (!outputUrl) return;
    try {
      const response = await fetch(outputUrl);
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'generated-ppl-image.png');
      document.body.appendChild(link);
      link.click();
      link.parentNode?.removeChild(link);
      window.URL.revokeObjectURL(url);
    } catch (error) {
      console.error("이미지 다운로드에 실패했습니다.", error);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!image1 || !image2) {
      alert("이미지 두 개를 모두 업로드하세요.");
      return;
    }

    const finalSeed = isRandomSeed ? Math.floor(Math.random() * 999999) : seed;

    const formData = new FormData();
    formData.append("prompt", prompt);
    formData.append("input_image_1", image1);
    formData.append("input_image_2", image2);
    formData.append("aspect_ratio", aspectRatio);
    formData.append("output_format", outputFormat);
    formData.append("safety_tolerance", safetyTolerance.toString());
    formData.append("seed", finalSeed.toString());

    setLoading(true);
    setOutputUrl("");

    try {
      // const response = await axios.post<{ output_url: string }>("http://localhost:8000/generate", formData); local test시 사용 코드
      const response = await axios.post("/api/ppl-gen/generate", formData); // azure 배포시 사용 코드
      setOutputUrl(response.data.output_url);
    } catch (error) {
      alert("이미지 생성에 실패했습니다. 입력 값이나 모델 상태를 확인해 주세요.");
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={containerStyle}>
      <NavBar />
      <div style={mainContentStyle}>
        <div style={formColumnStyle}>
          <h2 style={{ marginBottom: '30px' }}>웹툰 캐릭터 PPL 생성기</h2>
          <form onSubmit={handleSubmit}>
            <label htmlFor="prompt" style={{ fontWeight: "bold", display: 'block', marginBottom: '6px' }}>프롬프트를 영어로 입력하세요</label>
            <textarea
              id="prompt"
              value={prompt}
              onChange={(e) => setPrompt(e.target.value)}
              style={promptBoxStyle}
            />

            <label style={{ fontWeight: "bold", display: 'block', marginBottom: '8px' }}>① 캐릭터 이미지</label>
            <input type="file" accept="image/*" onChange={(e) => handleImageUpload(e.target.files?.[0] || null, setImage1, setPreview1)} style={fileInputBoxStyle} />
            <div style={imagePreviewFrameStyle}>
              {preview1 ? (
                <img src={preview1} alt="캐릭터 이미지" style={{ maxWidth: "100%", maxHeight: "300px" }} />
              ) : (
                <p>이미지 업로드 시 여기에 표시됩니다.</p>
              )}
            </div>

            <label style={{ fontWeight: "bold", display: 'block', marginBottom: '8px' }}>② 제품 이미지</label>
            <input type="file" accept="image/*" onChange={(e) => handleImageUpload(e.target.files?.[0] || null, setImage2, setPreview2)} style={fileInputBoxStyle} />
            <div style={imagePreviewFrameStyle}>
              {preview2 ? (
                <img src={preview2} alt="제품 이미지" style={{ maxWidth: "100%", maxHeight: "300px" }} />
              ) : (
                <p>이미지 업로드 시 여기에 표시됩니다.</p>
              )}
            </div>

            <label style={{ fontWeight: "bold", display: 'block', marginBottom: '8px' }}>이미지 비율</label>
            <select value={aspectRatio} onChange={(e) => setAspectRatio(e.target.value)} style={fileInputBoxStyle}>
              {aspectRatios.map(r => <option key={r} value={r}>{r}</option>)}
            </select>

            <label style={{ fontWeight: "bold", display: 'block', marginBottom: '8px' }}>출력 형식</label>
            <select value={outputFormat} onChange={(e) => setOutputFormat(e.target.value)} style={fileInputBoxStyle}>
              <option value="png">png</option>
              <option value="jpeg">jpeg</option>
            </select>

            <label style={{ fontWeight: "bold", display: 'block', marginTop: "20px" }}>Seed 설정</label>
            <div style={{ display: "flex", gap: "16px", marginTop: "8px" }}>
              <div>
                <input type="radio" id="fixedSeed" name="seedMode" checked={!isRandomSeed} onChange={() => setIsRandomSeed(false)} />
                <label htmlFor="fixedSeed" style={{ marginLeft: "4px" }}>고정값 사용</label>
              </div>
              <div>
                <input type="radio" id="randomSeed" name="seedMode" checked={isRandomSeed} onChange={() => setIsRandomSeed(true)} />
                <label htmlFor="randomSeed" style={{ marginLeft: "4px" }}>랜덤하게 생성</label>
              </div>
            </div>
            <input type="number" value={seed} onChange={(e) => setSeed(Number(e.target.value))} disabled={isRandomSeed} style={fileInputBoxStyle} />

            <label htmlFor="safetyTolerance" style={{ fontWeight: "bold" }}>Safety Tolerance(프롬프트 적용): {safetyTolerance}</label>
            <input id="safetyTolerance" type="range" min={0} max={2} step={1} value={safetyTolerance} onChange={(e) => setSafetyTolerance(Number(e.target.value))} style={{ width: '100%', marginTop: '8px', marginBottom: '16px' }} />

            <button type="submit" disabled={loading} style={{ ...generateButtonStyle, backgroundColor: loading ? "#ccc" : "#09AA5C" }}>
              {loading ? "생성 중..." : "PPL 이미지 생성✨"}
            </button>
          </form>
        </div>

        <div style={outputColumnStyle}>
          <h3 style={{ marginBottom: "10px" }}>출력 이미지</h3>
          <div style={imagePreviewFrameStyle}>
            {loading && <p>이미지를 생성 중입니다...</p>}
            {!loading && outputUrl ? (
              <img src={outputUrl} alt="출력 이미지" style={{ maxWidth: "100%", maxHeight: "400px", height: "auto" }} />
            ) : (
              !loading && <p style={{ color: "#aaa" }}>생성된 이미지가 여기에 표시됩니다.</p>
            )}
          </div>
          {outputUrl && !loading && (
            <button onClick={handleDownload} style={downloadButtonStyle}>
              📥 이미지 다운로드
            </button>
          )}
        </div>
      </div>
    </div>
  );
}
