import React, { useState } from "react";
import axios from "axios";
import NavBar from "./NavBar.tsx"; // NavBar 컴포넌트 경로 확인

const aspectRatios = [
  "match_input_image", "1:1", "16:9", "9:16", "4:3", "3:4", "3:2", "2:3",
  "4:5", "5:4", "21:9", "9:21", "2:1", "1:2"
];

// --- 스타일 객체 정의 ---
const containerStyle: React.CSSProperties = {
  fontFamily: "'Nanum Gothic', sans-serif",
  backgroundColor: "#f4f4f4",
  minHeight: "100vh",
};

const mainContentStyle: React.CSSProperties = {
  display: "flex",
  padding: "0px",
  gap: "40px", // 폼과 출력 사이의 간격
  maxWidth: "1440px",
  margin: "0 auto",
  alignItems: "flex-start",
};

const formColumnStyle: React.CSSProperties = {
  flex: 1,
  minWidth: "400px",
};

const outputColumnStyle: React.CSSProperties = {
  flex: 1,
  minWidth: "400px",
  textAlign: "center",
};

const imageFrameStyle: React.CSSProperties = {
  border: "1px solid #ddd",
  borderRadius: "8px",
  padding: "10px",
  marginBottom: "20px",
  backgroundColor: "#f9f9f9",
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
  minHeight: "300px",
  width: "100%",
};

const downloadButtonStyle: React.CSSProperties = {
  marginTop: "20px",
  padding: "12px 24px",
  fontWeight: "bold",
  backgroundColor: "#09AA5C",
  color: "white",
  border: "none",
  borderRadius: "5px",
  cursor: "pointer",
  fontSize: "16px",
};

export default function GenerateForm() {
  const [prompt, setPrompt] = useState("Draw the woman in the picture walking with this handbag.");
  const [image1, setImage1] = useState<File | null>(null);
  const [image2, setImage2] = useState<File | null>(null);
  const [preview1, setPreview1] = useState<string>("");
  const [preview2, setPreview2] = useState<string>("");
  const [aspectRatio, setAspectRatio] = useState("1:1");
  const [outputFormat, setOutputFormat] = useState("png");
  const [outputUrl, setOutputUrl] = useState("");
  const [loading, setLoading] = useState(false);
  const [seed, setSeed] = useState<number>(11);
  const [isRandomSeed, setIsRandomSeed] = useState(false);
  const [safetyTolerance, setSafetyTolerance] = useState(2);

  const handleImageUpload = (
    file: File | null,
    setImage: React.Dispatch<React.SetStateAction<File | null>>,
    setPreview: React.Dispatch<React.SetStateAction<string>>
  ) => {
    if (file) {
      setImage(file);
      setPreview(URL.createObjectURL(file));
    }
  };

  const handleDownload = async () => {
    if (!outputUrl) return;
    try {
      const response = await fetch(outputUrl);
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'generated-ppl-image.png');
      document.body.appendChild(link);
      link.click();
      link.parentNode?.removeChild(link);
      window.URL.revokeObjectURL(url);
    } catch (error) {
      console.error("이미지 다운로드에 실패했습니다.", error);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!image1 || !image2) {
      alert("이미지 두 개를 모두 업로드하세요.");
      return;
    }

    const finalSeed = isRandomSeed ? Math.floor(Math.random() * 999999) : seed;

    const formData = new FormData();
    formData.append("prompt", prompt);
    formData.append("input_image_1", image1);
    formData.append("input_image_2", image2);
    formData.append("aspect_ratio", aspectRatio);
    formData.append("output_format", outputFormat);
    formData.append("safety_tolerance", safetyTolerance.toString());
    formData.append("seed", finalSeed.toString());

    setLoading(true);
    setOutputUrl("");

    try {
      const response = await axios.post<{ output_url: string }>("http://localhost:8000/generate", formData);
      setOutputUrl(response.data.output_url);
    } catch (error) {
      alert("이미지 생성에 실패했습니다. 입력 값이나 모델 상태를 확인해 주세요.");
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={containerStyle}>
      <NavBar />
      <div style={mainContentStyle}>
        {/* Left Input Panel */}
        <div style={formColumnStyle}>
          <h2 style={{ marginBottom: '30px' }}>웹툰 캐릭터 PPL 생성기</h2>
          <form onSubmit={handleSubmit}>
            <label htmlFor="prompt">프롬프트</label>
            <textarea
              id="prompt"
              value={prompt}
              onChange={(e) => setPrompt(e.target.value)}
              style={{ width: '100%', padding: '8px', marginBottom: '16px', borderRadius: '4px', border: '1px solid #ccc', minHeight: '80px' }}
            />

            <label>① 캐릭터 이미지</label>
            <input
              type="file"
              accept="image/*"
              onChange={(e) => handleImageUpload(e.target.files?.[0] || null, setImage1, setPreview1)}
              style={{ width: '100%', marginBottom: '8px' }}
            />
            <div style={imageFrameStyle}>
              {preview1 ? (
                <img src={preview1} alt="캐릭터 이미지" style={{ maxWidth: "100%", maxHeight: "300px" }} />
              ) : (
                <p style={{ color: "#aaa" }}>이미지 업로드 시 여기에 표시됩니다.</p>
              )}
            </div>

            <label>② 제품 이미지</label>
            <input
              type="file"
              accept="image/*"
              onChange={(e) => handleImageUpload(e.target.files?.[0] || null, setImage2, setPreview2)}
              style={{ width: '100%', marginBottom: '8px' }}
            />
            <div style={imageFrameStyle}>
              {preview2 ? (
                <img src={preview2} alt="제품 이미지" style={{ maxWidth: "100%", maxHeight: "300px" }} />
              ) : (
                <p style={{ color: "#aaa" }}>이미지 업로드 시 여기에 표시됩니다.</p>
              )}
            </div>

            <label>이미지 비율</label>
            <select
              value={aspectRatio}
              onChange={(e) => setAspectRatio(e.target.value)}
              style={{ width: '100%', padding: '8px', marginBottom: '16px' }}
            >
              {aspectRatios.map(r => <option key={r} value={r}>{r}</option>)}
            </select>

            <label>출력 형식</label>
            <select
              value={outputFormat}
              onChange={(e) => setOutputFormat(e.target.value)}
              style={{ width: '100%', padding: '8px', marginBottom: '16px' }}
            >
              <option value="png">png</option>
              <option value="jpeg">jpeg</option>
            </select>

            <label>Seed 설정</label>
            <div style={{ marginBottom: '8px' }}>
              <input type="radio" id="fixedSeed" name="seedMode" checked={!isRandomSeed} onChange={() => setIsRandomSeed(false)} />
              <label htmlFor="fixedSeed" style={{ marginRight: '16px' }}>고정값 사용</label>
              <input type="radio" id="randomSeed" name="seedMode" checked={isRandomSeed} onChange={() => setIsRandomSeed(true)} />
              <label htmlFor="randomSeed">랜덤하게 생성</label>
            </div>
            <input
              type="number"
              value={seed}
              onChange={(e) => setSeed(Number(e.target.value))}
              disabled={isRandomSeed}
              style={{ width: '100%', padding: '8px', marginBottom: '16px' }}
            />

            <label>안전성 수치: {safetyTolerance}</label>
            <input
              type="range"
              min={0}
              max={2}
              step={1}
              value={safetyTolerance}
              onChange={(e) => setSafetyTolerance(Number(e.target.value))}
              style={{ width: '100%', marginBottom: '16px' }}
            />

            <button
              type="submit"
              disabled={loading}
              style={{ width: '100%', padding: '12px', backgroundColor: loading ? '#ccc' : '#09AA5C', color: 'white', fontWeight: 'bold', borderRadius: '5px' }}
            >
              {loading ? "생성 중..." : "이미지 생성"}
            </button>
          </form>
        </div>

        {/* Right Output Panel */}
        <div style={outputColumnStyle}>
          <h3>출력 이미지</h3>
          <div style={imageFrameStyle}>
            {loading && <p>이미지를 생성 중입니다...</p>}
            {!loading && outputUrl ? (
              <img src={outputUrl} alt="출력 이미지" style={{ maxWidth: "100%", maxHeight: "400px" }} />
            ) : (
              !loading && <p style={{ color: "#aaa" }}>생성된 이미지가 여기에 표시됩니다.</p>
            )}
          </div>
          {outputUrl && !loading && (
            <button onClick={handleDownload} style={downloadButtonStyle}>
              📥 이미지 다운로드
            </button>
          )}
        </div>
      </div>
    </div>
  );
}
